import React from 'react'

class Task extends React.Component {

    deleteTask = () => {
        this.props.deleteTask(this.props.text)
    }

    render() {
        return (
            <div>
                <div className="task">
                    <div className="label">
                        {this.props.category}
                    </div>
                    <div className="text">
                        {this.props.text}
                    </div>
                    <button className="delete" onClick={this.props.deleteTask}>X</button>
                </div>
            </div>
        )
    }
}

export default Task
