import React from 'react';
import './App.css';
import { CATEGORIES } from './data'
import TaskContainer from './Containers/TaskContainer.js'
import ButtonContainer from './Containers/ButtonContainer.js'

class App extends React.Component {

  state = {
    selectedCategory: 'All',
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ],
    categories: CATEGORIES,
    catFiltered: []
  }

  filteredTasks = () =>
  this.state.tasks.filter(
    t =>
      this.state.selectedCategory === 'All' ||
      t.category === this.state.selectedCategory
  )

  buttonSelect = () => {
    return this.state.tasks.filter((task) => {
      let filterArr = [...this.state.tasks]

      this.setState({
        catFiltered: filterArr
      })
      if(task.category === this.state.defaultSelect) {
        let filterArr = [...this.state.tasks]
        this.setState({
          catFiltered: filterArr
        })
      } 

    }
    // e.persist()

    // e.persist()
    // this.state.tasks.filter(task => task.category === "All" || task.category === e.target.textContent)

    )
  }

  // deleteTask = (task) => {
