  //   let filterArr = this.state.tasks.filter(t => t.text !== task)
  //   taskObj = filterArr.find(t => t.text == task)
  //   this.setState({
  //     catFiltered: filterArr
  //   })
    // debugger
    // this.setState({
    //   catFiltered: filterArr
    // })
  // }

  render() {
    return (
      <div className="App">
        <h2>My tasks</h2>
        <ButtonContainer categories={this.state.categories} buttonSelect={this.buttonSelect} />
        {/* <TaskContainer deleteTask={this.deleteTask} tasks={this.state.catFiltered} /> */}
        <TaskContainer deleteTask={this.deleteTask} tasks={this.buttonSelect} />
      </div>
    );
  }
}


export default App;
