import React from 'react';
import './App.css';
import { CATEGORIES } from './data'
import TaskContainer from './Containers/TaskContainer.js'
import ButtonContainer from './Containers/ButtonContainer.js'

class App extends React.Component {

  state = {
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ],
    categories: CATEGORIES,
    catFiltered: []
  }

  buttonSelect = (e) => {
    e.persist()
    let filterArr = this.state.tasks.filter(task => task.category === "All" || task.category === e.target.textContent)
      this.setState({
        catFiltered: filterArr
      })
    if(e.target.textContent === "All") {
      let filterArr = [...this.state.tasks]
      this.setState({
        catFiltered: filterArr
      })
    } 
  }

  deleteTask = (e) => {
    e.persist()
    // debugger
    this.setState({
      tasks: this.state.tasks.filter(
        t => {
          let task = e.target.previousElementSibling.innerText
          let category = e.target.previousElementSibling.previousElementSibling.innerText
          !(t.text === task.text && t.category === task.category)
        }
      )
    })
  }

  render() {
    return (
      <div className="App">
        <h2>My tasks</h2>
        <ButtonContainer categories={this.state.categories} buttonSelect={this.buttonSelect} />
        <TaskContainer deleteTask={this.deleteTask} tasks={this.state.catFiltered} />
      </div>
    );
  }
}


export default App;
